import Header from "./components/Header/Header";
import Body from "./components/Body/Body";
import React, { useState } from "react";

function App() {

  [imagesDisplayed, setImagesDisplayed] = useState(false);

  const switchBetweenImagesAndMessages= () => {
    setImagesDisplayed(() => {return !imagesDisplayed});
    console.log(imagesDisplayed);
  }

  return (
    <div>
      <Header onUpdateBody={switchBetweenImagesAndMessages}/>
      <Body />
    </div>
  );
}

export default App;
